import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';

// URL dasar API
const API_BASE_URL = "http://localhost:3001/api"; 

function DashboardPage() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  // State untuk menyimpan data presensi dari server
  const [presenceData, setPresenceData] = useState({
        checkIn: null,
        checkOut: null,
    });
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isAuthReady, setIsAuthReady] = useState(false); // Penanda otentikasi selesai

    /**
     * Helper untuk memformat waktu ke format ID (Jakarta Timezone)
     */
    const formatTime = (isoString) => {
        if (!isoString) return "-";
        // Pastikan input adalah string ISO untuk dibuat objek Date
        return new Date(isoString).toLocaleString("id-ID", {
            timeZone: "Asia/Jakarta",
            hour: '2-digit',
            minute: '2-digit',
            day: 'numeric',
            month: 'short',
        });
    };

    /**
     * Mengambil status presensi hari ini dari server.
     */
    const fetchPresenceStatus = async (token) => {
        if (!token) return;

        setLoading(true);
        setError('');
        setMessage('');

        try {
            const response = await axios.get(
                `${API_BASE_URL}/presensi/status`, // Endpoint untuk mendapatkan status hari ini
                {
                    headers: { Authorization: `Bearer ${token}` }
                }
            );

            const statusData = response.data; // Asumsi response.data berisi { checkIn: isoTime, checkOut: isoTime | null }
            setPresenceData(statusData);
            
            if (statusData.checkIn && statusData.checkOut) {
                setMessage("Presensi hari ini sudah selesai.");
            } else if (statusData.checkIn) {
                setMessage("Anda sudah Check-In. Silakan Check-Out.");
            } else {
                setMessage("Siap untuk Check-In.");
            }

        } catch (err) {
            const status = err.response ? err.response.status : null;
            if (status === 401 || status === 403) {
                // Token tidak valid atau kedaluarsa
                setError('Token tidak valid atau kedaluarsa. Silakan login kembali.');
                setTimeout(handleLogout, 2000);
            } else {
                // Error lainnya, mungkin belum ada data presensi hari ini
                setError(err.response?.data?.message || 'Gagal memuat status presensi.');
            }
        } finally {
            setLoading(false);
        }
    };


  const handleCheckIn = async () => {
    setLoading(true);
    setError('');
    setMessage('');

    try {
      const token = localStorage.getItem('token');
      // PERBAIKAN: Menggunakan endpoint /checkin (tanpa strip)
      const response = await axios.post(
        `${API_BASE_URL}/presensi/checkin`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      setMessage(response.data.message);
      // Setelah berhasil, muat ulang status presensi
      fetchPresenceStatus(token);

    } catch (err) {
        const status = err.response ? err.response.status : null;
        if (status === 401 || status === 403) {
            setError('Sesi berakhir. Harap login kembali.');
            setTimeout(handleLogout, 2000);
        } else {
            setError(err.response?.data?.message || 'Check-in gagal');
        }
    } finally {
      setLoading(false);
    }
  };

  const handleCheckOut = async () => {
    setLoading(true);
    setError('');
    setMessage('');

    try {
      const token = localStorage.getItem('token');
      // PERBAIKAN: Menggunakan endpoint /checkout (tanpa strip)
      const response = await axios.post(
        `${API_BASE_URL}/presensi/checkout`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );

      setMessage(response.data.message);
      // Setelah berhasil, muat ulang status presensi
      fetchPresenceStatus(token);

    } catch (err) {
        const status = err.response ? err.response.status : null;
        if (status === 401 || status === 403) {
            setError('Sesi berakhir. Harap login kembali.');
            setTimeout(handleLogout, 2000);
        } else {
            setError(err.response?.data?.message || 'Check-out gagal');
        }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

    // Efek untuk memproses Token saat komponen dimuat
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);
      // Cek kadaluarsa
      if (decoded.exp * 1000 < Date.now()) {
          setError('Token kadaluarsa. Silakan login ulang.');
          setTimeout(handleLogout, 1000);
          return;
      }
      setUserData(decoded);
      setIsAuthReady(true); // Otentikasi sukses
    } catch (err) {
      console.error('Token tidak valid:', err);
      setError('Token tidak valid. Silakan login ulang.');
      navigate('/login');
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);


    // Efek untuk memuat status presensi HANYA setelah otentikasi selesai
    useEffect(() => {
        if (isAuthReady) {
            const token = localStorage.getItem('token');
            fetchPresenceStatus(token);
        }
    }, [isAuthReady]); // Jalankan hanya saat isAuthReady menjadi true


  if (!userData) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-xl">Memuat...</div>
      </div>
    );
  }
  
    // Logika tampilan berdasarkan data presensi
    const hasCheckedIn = !!presenceData.checkIn;
    const hasCheckedOut = !!presenceData.checkOut;


  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-8">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-800">
                Dashboard Presensi
              </h1>
              <p className="text-gray-600 mt-2">
                Selamat datang, <span className="font-semibold">{userData.nama}</span>
              </p>
              <p className="text-sm text-gray-500">
                Role: <span className="font-medium capitalize">{userData.role}</span>
              </p>
            </div>
            <button
              onClick={handleLogout}
              className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors shadow-md"
            >
              Logout
            </button>
          </div>
        </div>

        {/* Status Presensi */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h2 className="text-xl font-bold text-gray-800 mb-4 border-b pb-2">
            Status Presensi Hari Ini
          </h2>

          {loading && (
            <div className="flex items-center justify-center p-4 text-indigo-600 bg-indigo-50 rounded-lg mb-4">
              <svg className="animate-spin -ml-1 mr-3 h-5 w-5" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Memuat status...
            </div>
          )}

          {/* Menampilkan status berdasarkan data server */}
          {!loading && hasCheckedIn && !hasCheckedOut ? (
            <div className="bg-green-50 border-l-4 border-green-500 p-4 mb-4 rounded-md">
              <p className="text-green-700 font-medium mb-1">
                Anda sudah check-in. Saatnya check-out!
              </p>
              <p className="text-sm text-gray-700">
                <span className="font-medium">Waktu Check-in:</span> {formatTime(presenceData.checkIn)}
              </p>
            </div>
          ) : hasCheckedOut ? (
            <div className="bg-blue-50 border-l-4 border-blue-500 p-4 mb-4 rounded-md">
              <p className="text-blue-700 font-medium mb-1">
                Presensi hari ini sudah selesai.
              </p>
              <p className="text-sm text-gray-700">
                <span className="font-medium">Waktu Check-in:</span> {formatTime(presenceData.checkIn)}
                <span className="ml-4 font-medium">Waktu Check-out:</span> {formatTime(presenceData.checkOut)}
              </p>
            </div>
          ) : !loading && (
            <div className="bg-yellow-50 border-l-4 border-yellow-500 p-4 mb-4 rounded-md">
              <p className="text-yellow-700 font-medium">
                Anda belum melakukan check-in hari ini.
              </p>
            </div>
          )}

          {/* Messages */}
          {message && !loading && (
            <div className="bg-green-100 border border-green-300 p-4 mb-4 rounded-md">
              <p className="text-green-800 font-medium">{message}</p>
            </div>
          )}

          {error && !loading && (
            <div className="bg-red-100 border border-red-300 p-4 mb-4 rounded-md">
              <p className="text-red-800 font-medium">{error}</p>
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex gap-4 mt-6">
            {!hasCheckedIn ? (
              <button
                onClick={handleCheckIn}
                disabled={loading}
                className="flex-1 py-3 px-6 bg-green-600 text-white font-semibold rounded-lg shadow-md hover:bg-green-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors transform hover:scale-[1.01]"
              >
                {loading ? 'Processing...' : 'Check-In'}
              </button>
            ) : !hasCheckedOut ? (
              <button
                onClick={handleCheckOut}
                disabled={loading}
                className="flex-1 py-3 px-6 bg-red-600 text-white font-semibold rounded-lg shadow-md hover:bg-red-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors transform hover:scale-[1.01]"
              >
                {loading ? 'Processing...' : 'Check-Out'}
              </button>
            ) : (
              <div className="flex-1 py-3 px-6 bg-gray-200 text-gray-600 font-semibold rounded-lg text-center shadow-inner">
                Presensi hari ini sudah selesai
              </div>
            )}
          </div>
        </div>

        {/* Info Card */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-bold text-gray-800 mb-3">
            Informasi
          </h3>
          <ul className="space-y-2 text-sm text-gray-600">
            <li className="flex items-start">
              <span className="mr-2">•</span>
              <span>Check-in hanya dapat dilakukan sekali per hari</span>
            </li>
            <li className="flex items-start">
              <span className="mr-2">•</span>
              <span>Pastikan untuk check-out sebelum meninggalkan tempat kerja</span>
            </li>
            <li className="flex items-start">
              <span className="mr-2">•</span>
              <span>Data presensi akan tersimpan secara otomatis</span>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default DashboardPage;
